import React, {Component} from "react";
import {BrowserRouter as Router,
				Route,
    		Switch,
    		useParams} from "react-router-dom";

import {Header} from "./Header";
import {Footer} from "./Footer";
import {Jumbotron} from "./Jumbotron";
import {Data} from "./Data"

import {BuyerView} from "./BuyerView";
import {SellerView} from "./SellerView";
import {ItemView} from "./ItemView";

class App extends Component {

  state = {
    itemCount:0,
    totalPrice:0,
    addedItems:[],
    checkout: false,
    items : Data.ItemList,
    sellerItems : Data.SellerItemList,
    displayAddItem: false,
    imageFileCount: 1,
    editItem : null
  };


  /********************************************
  *
  * Functions in use for Buyer View
  *
  *********************************************/
  /** 
   * Toggle item for purchase
   */
   toggleItemAddition = index => {
      this.setState({
        items: this.state.items.map((item, itemIndex) => {

         if(itemIndex === index) {
          if (item.isAdded) {
            this.state.itemCount = this.state.itemCount - 1
            this.state.totalPrice = this.state.totalPrice - parseInt(item.price)
            var filteredItems = this.state.addedItems.filter((elem) => elem.id !== item.id);
            this.state.addedItems = filteredItems

          } else {
            this.state.itemCount = this.state.itemCount + 1
            this.state.totalPrice = this.state.totalPrice + parseInt(item.price)
            this.state.addedItems.push(item)
          }

          return {
            ...item,
            isAdded: !item.isAdded
          }
         } 
         return item;

        }) 
      });
   }

  /**
   * Function to handle checkout process
   * @param event
   */
  onClickCheckout = (event) => {
    this.setState({
      ...this.state.checkout = true
    });
  };

  /**
   * Function to handle checkout process
   * @param event
   */
  toggleBuyButton = (event) => {
    this.setState({
      ...this.state.checkout = !this.state.checkout
    });
  };

	/**
   * Function to handle payment process
   * @param event
   */
  handlePayment = (event) => {

  };

  /**
   * Function to remove items from checkout list
   * @param event
   */
  removeCheckoutItem = index => {
    this.setState({
      items: this.state.addedItems.map((item, itemIndex) => {
        if(itemIndex === index) {
          this.state.itemCount = this.state.itemCount - 1
          this.state.totalPrice = this.state.totalPrice - parseInt(item.price)
          var filteredItems = this.state.addedItems.filter((elem) => elem.id !== item.id);
          this.state.addedItems = filteredItems
        }
      }) 
    })
  };


  /********************************************
  *
  * Functions in use for Seller View
  *
  *********************************************/

	/**
   * Function to handle payment process
   * @param event
   */
  toggleAddItem = (event) => {
    this.setState({
      ...this.state.displayAddItem = !this.state.displayAddItem
    })
  };

  handleEditItem = index => {
  	this.state.displayAddItem = true  
    this.state.editItem = this.state.sellerItems[index]
    console.log("Editing item " + this.state.editItem.name);

  };

	handleSubmitItem = (event) => {

  };
  handleFileCount = (event) => {
  	this.setState({
      ...this.state.imageFileCount = this.state.imageFileCount + 1
    })

  };
  /********************************************
  *
  * Functions in use for Item Details View
  *
  *********************************************/
	/**
   * Function to handle payment process
   * @param event
   */

  /**
  * Function to display ItemDetails
  */
	Item = () => {
    // We can use the `useParams` hook here to access
    // the dynamic pieces of the URL.
    let { id } = useParams();
  	var item = Data.ItemList[id-1]

    return (
      <ItemView
      	item={item}
      	toggleBuy={this.state.checkout}
      	onClickBuy={this.toggleBuyButton}
      	onClickPay={this.handlePayment}
       />
    );
  }

  /**
   * Main method to render the page
   */
  render() {

    return <div>
      <Header/>
      <Jumbotron/>
      <Router>
	      <Switch>
		      <Route exact path="/">
		          <BuyerView 
				     		state = {this.state}
				     		toggleItemAddition = {this.toggleItemAddition}
				     		onClickCheckout = {this.onClickCheckout}
				     		removeCheckoutItem = {this.removeCheckoutItem} 
				     		onClickPay = {this.handlePayment}
				     	/>
		      </Route>
		      <Route exact path="/seller">
	          <SellerView 
			     		state  = {this.state}
			     		addItem = {this.toggleAddItem}
			     		editItem = {this.handleEditItem}
			     		submitItem = {this.handleSubmitItem}
			     		handleFileCount = {this.handleFileCount}

			     	/>
		      </Route>
		      <Route path="/:id" children={<this.Item />}></Route>
	   	 	</Switch>
   	 	</Router>     	

      <hr/>

      <Footer/>

    </div>;
  }
}

export default App;
