import React, {Component} from 'react';
import {ItemCard} from "./ItemCard";
import {Header} from "./Header";
import {Footer} from "./Footer";
import {Jumbtron} from "./Jumbtron";
import image from "./img/item1.jpg"

class App extends Component {
  state = {
    name: "",
    price: "",
    img:"",
    items: [
      {
        id: 1,
        name: "Model 1",
        price: "150",
        img: "item1.jpg"
      },
      {
        id: 2,
        name: "Model 2",
        price: "100",
        img: "item2.jpg"
      },
      {
        id: 3,
        name: "Model 3",
        price: "140",
        img: "item3.jpg"
      },
      {
        id: 4,
        name: "Model 4",
        price: "200",
        img: "item4.jpg"
      },
      {
        id: 5,
        name: "Model 5",
        price: "200",
        img: "item5.jpg"
      },
      {
        id: 6,
        name: "Model 6",
        price: "200",
        img: "item6.jpg"
      },
      {
        id: 7,
        name: "Model 7",
        price: "200",
        img: "item7.jpg"
      },
      {
        id: 8,
        name: "Model 8",
        price: "200",
        img: "item8.jpg"
      },
      {
        id: 9,
        name: "Model 9",
        price: "200",
        img: "item9.jpg"
      },
      {
        id: 10,
        name: "Model 10",
        price: "200",
        img: "item10.jpg"
      }

    ]
  };

  /**
   * Handle input changes in the AddItem component.
   * @param event
   */
  handleInputChange = event => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  };

  /* 
   * Toggle item for Editing
   */
   toggleItemEditing = index => {
      this.setState({
        items: this.state.items.map((item, itemIndex) => {

         if(itemIndex === index) {
          return {
            ...item,
            isEditing: !item.isEditing
          }
         } 
         return item;

        }) 
      });
   }


  /**
   * Add a new item
   * @param event
   */
  addItem = event => {
    event.preventDefault();
    const {name, price,img} = this.state;
    const itemsInState = this.state.items;
    const itemsArrayLength = itemsInState.length;
    const id = itemsArrayLength ? (itemsInState[itemsArrayLength - 1].id + 1) : 1;
    this.setState({
      items: [
        ...itemsInState,
        Object.assign({}, {
          id,
          name,
          price,
          img
        })
      ],
      name: "",
      price: "",
      img: ""
    })
  };

  /**
   * Function to handle item updates
   * @param event, index
   */
  handleItemUpdate = (event, index) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState( {
      items: this.state.items.map((item, itemIndex) => {
        if (itemIndex === index) {
          return {
            ...item,
            [name] : value
          }
        }
        return item;
      })
    });
  };

  /**
   * Function to delete item
   * @param index
   */
   onCancel = index => {
    this.setState( {
      items : [
      ...this.state.items.slice(0, index),
      ...this.state.items.slice(index + 1),
      ]
    });
   };

  render() {

    return <div>
      <Header/>

      <Jumbtron/>

      <div className="container pt-4">

        <h1 className="display-4 my-4 text-center text-muted">Assets</h1>

        <div className="row">
          {
            this.state.items.map((item, index) =>
                <ItemCard
                    key = {item.id}
                    index = {index}
                    item = {item}
                    toggleEditing = {() => this.toggleItemEditing(index)}
                    onChangeUpdate = {event => this.handleItemUpdate(event, index)}
                    onCancel = {() => this.onCancel(index)}
                />
            )
          }
        </div>

        <hr/>
        <Footer/>
      </div>
    </div>;
  }
}

export default App;
