import React from 'react';
import PropTypes from 'prop-types';
import {Payment} from "./Payment"

export const Checkout = ({itemCount, itemPrice, checkout=false, onClickCheckout}) => (
      <div className="container mr-2">
        <h4 className="text-top text-muted">Assets Purchased</h4>
        {
            checkout?<div className="row"><Payment itemPrice={itemPrice}/></div>

            :

        <div className="row justify-content-end">
         <span className="text-md-center">
          {itemCount > 0 && !checkout ?
            <span className="text-md-center">{itemCount} Items @ ${itemPrice}</span>
            :
            <span/> 
          }
          {itemCount > 0 ?
          <button type="button" className="btn btn-primary ml-2" onClick={onClickCheckout} >Checkout</button>
          :
          <button type="button" className="btn btn-muted ml-2">Checkout</button>
          }
          </span>

        </div>
      }
      </div>
);

Checkout.propTypes = {
  itemCount: PropTypes.number.isRequired,
  itemPrice: PropTypes.number.isRequired,
  onClickCheckout: PropTypes.func.isRequired
}
