import React, {Component} from 'react';
import {ItemCard} from "./ItemCard";
import {Header} from "./Header";
import {Footer} from "./Footer";
import {Jumbtron} from "./Jumbtron";
import {Checkout} from "./Checkout";
import {PurchasedItemCard} from "./PurchasedItemCard";

class App extends Component {
  state = {
    itemCount:0,
    totalPrice:0,
    addedItems:[],
    checkout: false,
    items: [
      {
        id: 1,
        name: "Model 1",
        price: "150",
        img: "item1.jpg",
        isAdded: false
      },
      {
        id: 2,
        name: "Model 2",
        price: "100",
        img: "item2.jpg",
        isAdded: false

      },
      {
        id: 3,
        name: "Model 3",
        price: "140",
        img: "item3.jpg",
        isAdded: false
      },
      {
        id: 4,
        name: "Model 4",
        price: "200",
        img: "item4.jpg",
        isAdded: false
      },
      {
        id: 5,
        name: "Model 5",
        price: "190",
        img: "item5.jpg",
        isAdded: false
      },
      {
        id: 6,
        name: "Model 6",
        price: "205",
        img: "item6.jpg",
        isAdded: false
      },
      {
        id: 7,
        name: "Model 7",
        price: "210",
        img: "item7.jpg",
        isAdded: false
      },
      {
        id: 8,
        name: "Model 8",
        price: "150",
        img: "item8.jpg",
        isAdded: false
      },
      {
        id: 9,
        name: "Model 9",
        price: "240",
        img: "item9.jpg",
        isAdded: false
      },
      {
        id: 10,
        name: "Model 10",
        price: "250",
        img: "item10.jpg",
        isAdded: false
      }

    ]
  };

  /**
   * Handle input changes in the AddItem component.
   * @param event
   */
  handleInputChange = event => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  };

  /* 
   * Toggle item for Editing
   */
   toggleItemAddition = index => {
      this.setState({
        items: this.state.items.map((item, itemIndex) => {

         if(itemIndex === index) {
          if (item.isAdded) {
            this.state.itemCount = this.state.itemCount - 1
            this.state.totalPrice = this.state.totalPrice - parseInt(item.price)
            var filteredItems = this.state.addedItems.filter((elem) => elem.id !== item.id);
            this.state.addedItems = filteredItems

          } else {
            this.state.itemCount = this.state.itemCount + 1
            this.state.totalPrice = this.state.totalPrice + parseInt(item.price)
            this.state.addedItems.push(item)
          }

          return {
            ...item,
            isAdded: !item.isAdded
          }
         } 
         return item;

        }) 
      });
   }


  /**
   * Function to handle item updates
   * @param event, index
   */
  handleItemUpdate = (event, index) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState( {
      items: this.state.items.map((item, itemIndex) => {
        if (itemIndex === index) {
          return {
            ...item,
            [name] : value
          }
        }
        return item;
      })
    });
  };

  onClickCheckout = (event) => {
    this.setState({
      ...this.state.checkout = true
    });
  };

  /**
   * Function to delete item
   * @param index
   */
   onCancel = index => {
    this.setState( {
      items : [
      ...this.state.items.slice(0, index),
      ...this.state.items.slice(index + 1),
      ]
    });
   };

  render() {

    return <div>
      <Header/>

      <Jumbtron/>
      { this.state.checkout ? 
        <div className="row">
          {
            this.state.purchasedItems.map((item, index) =>
                <PurchasedItemCard
                    key = {item.id}
                    index = {index}
                    item = {item}
                    toggleAddition = {() => this.toggleItemAddition(index)}
                    onChangeUpdate = {() => this.onCancel(index)}
                />
            )
          }
        </div>
      :
      <div className="container pt-4">
        <h2 className="display-4 text-center text-muted">Asset Catalog</h2>

        <div className="row my-4">
          <Checkout itemCount={this.state.itemCount} itemPrice={this.state.totalPrice} onClickCheckout={this.onClickCheckout} />
        </div>

        <div className="row">
          {
            this.state.items.map((item, index) =>
                <ItemCard
                    key = {item.id}
                    index = {index}
                    item = {item}
                    toggleAddition = {() => this.toggleItemAddition(index)}
                    onChangeUpdate = {() => this.onCancel(index)}
                />
            )
          }
        </div>
        <div className="row">
          <Checkout itemCount={this.state.itemCount} itemPrice={this.state.totalPrice} onClickCheckout={this.onClickCheckout} />
        </div>
      </div>
      }

      <hr/>
      <Footer/>

    </div>;
  }
}

export default App;
