import React, {Component} from 'react';
import image from './img/vumbulaitem.png';
import {ItemCard} from "./ItemCard";
import {Footer} from "./Footer";
import {Nav} from "./Nav";
import {Jumbtron} from "./Jumbtron";
import {AddItem} from "./AddItem";

class App extends Component {
  state = {
    name: "",
    price: "",
    items: [
      {
        id: 1,
        name: "Noodles",
        price: "15"
      },
      {
        id: 2,
        name: "Mangoes",
        price: "10"
      },
      {
        id: 3,
        name: "Oranges",
        price: "20"
      },
      {
        id: 4,
        name: "Passion Fruits",
        price: "14"
      }
    ]
  };

  /**
   * Handle input changes in the AddItem component.
   * @param event
   */
  handleInputChange = event => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  };

  /* 
   * Toggle item for Editing
   */
   toggleItemEditing = index => {
      this.setState({
        items: this.state.items.map((item, itemIndex) => {

         if(itemIndex === index) {
          return {
            ...item,
            isEditing: !item.isEditing
          }
         } 
         return item;

        }) 
      });
   }


  /**
   * Add a new item
   * @param event
   */
  addItem = event => {
    event.preventDefault();
    const {name, price} = this.state;
    const itemsInState = this.state.items;
    const itemsArrayLength = itemsInState.length;
    const id = itemsArrayLength ? (itemsInState[itemsArrayLength - 1].id + 1) : 1;
    this.setState({
      items: [
        ...itemsInState,
        Object.assign({}, {
          id,
          name,
          price
        })
      ],
      name: "",
      price: ""
    })
  }

  /**
   * Function to handle item updates
   * @param event, index
   */
  handleItemUpdate = (event, index) => {
    const target = event.target;
    const value = event.value;
    const name = event.name;
    this.setState( {
      items: this.state.itemsArrayLength.map((item, itemIndex) => {
        if (itemIndex === index) {
          return {
            ...item,
            [name] : value
          }
        }
        return item;
      })
    });
  };

  render() {
    const {name, price} = this.state;
    return <div>
      <Nav/>

      <Jumbtron/>

      <div className="container pt-4">

        <AddItem
            name={name}
            price={price}
            onChange={this.handleInputChange}
            onSubmit={this.addItem}
        />

        <h1 className="display-4 my-4 text-center text-muted">Items</h1>

        <div className="row">
          {
            this.state.items.map((item, index) =>
                <ItemCard
                    key={item.id}
                    index={index}
                    image={image}
                    item={item}
                    toggleEditing={() => this.toggleItemEditing(index)}
                />
            )
          }
        </div>

        <hr/>
        <Footer/>
      </div>
    </div>;
  }
}

export default App;
