import React, {Component} from "react";
import {useParams} from "react-router";

import {Header} from "./Header";
import {Footer} from "./Footer";
import {Jumbtron} from "./Jumbtron";
import {ItemCard} from "./ItemCard";
import {Checkout} from "./Checkout";
import {ItemDetails} from "./ItemDetails";
import {PurchasedItem} from "./PurchasedItem";
import {Payment} from "./Payment";

class App extends Component {

  state = {
    itemCount:0,
    totalPrice:0,
    addedItems:[],
    checkout: false,
    selectedItemFlag: false,
    selectedItemIndex: -1,
    items: [
      {
        id: 1,
        name: "Model 1",
        price: "150",
        img: ["item1.jpg","item1.jpg","item1.jpg",],
        isAdded: false
      },
      {
        id: 2,
        name: "Model 2",
        price: "100",
        img: ["item2.jpg"],
        isAdded: false

      },
      {
        id: 3,
        name: "Model 3",
        price: "140",
        img: ["item3.jpg"],
        isAdded: false
      },
      {
        id: 4,
        name: "Model 4",
        price: "200",
        img: ["item4.jpg"],
        isAdded: false
      },
      {
        id: 5,
        name: "Model 5",
        price: "190",
        img: ["item5.jpg"],
        isAdded: false
      },
      {
        id: 6,
        name: "Model 6",
        price: "205",
        img: ["item6.jpg"],
        isAdded: false
      },
      {
        id: 7,
        name: "Model 7",
        price: "210",
        img: ["item7.jpg"],
        isAdded: false
      },
      {
        id: 8,
        name: "Model 8",
        price: "150",
        img: ["item8.jpg"],
        isAdded: false
      },
      {
        id: 9,
        name: "Model 9",
        price: "240",
        img: ["item9.jpg"],
        isAdded: false
      },
      {
        id: 10,
        name: "Model 10",
        price: "250",
        img: ["item10.jpg"],
        isAdded: false
      }

    ]
  };

  /**
   * Handle input changes in the AddItem component.
   * @param event
   */
  handleInputChange = event => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  };

  /** 
   * Toggle item for Editing
   */
   toggleItemAddition = index => {
      this.setState({
        items: this.state.items.map((item, itemIndex) => {

         if(itemIndex === index) {
          if (item.isAdded) {
            this.state.itemCount = this.state.itemCount - 1
            this.state.totalPrice = this.state.totalPrice - parseInt(item.price)
            var filteredItems = this.state.addedItems.filter((elem) => elem.id !== item.id);
            this.state.addedItems = filteredItems

          } else {
            this.state.itemCount = this.state.itemCount + 1
            this.state.totalPrice = this.state.totalPrice + parseInt(item.price)
            this.state.addedItems.push(item)
          }

          return {
            ...item,
            isAdded: !item.isAdded
          }
         } 
         return item;

        }) 
      });
   }

  /**
   * Function to display item details
   * @param event, index
   */
  displayItemDetails = (event, index) => {

      const { id } = useParams();
      console.log(id);

      this.setState( {
      items: this.state.items.map((item, itemIndex) => {
        if (itemIndex === index) {
          this.state.selectedItemIndex = itemIndex
          this.state.selectedItem = item
        }
        return item;
      })
    });
  };

  /**
   * Function to handle checkout process
   * @param event
   */
  onClickCheckout = (event) => {
    this.setState({
      ...this.state.checkout = true
    });
  };

  /**
   * Function to remove items from checkout list
   * @param event
   */
  removeCheckoutItem = index => {
    this.setState({
      items: this.state.addedItems.map((item, itemIndex) => {
        if(itemIndex === index) {
          this.state.itemCount = this.state.itemCount - 1
          this.state.totalPrice = this.state.totalPrice - parseInt(item.price)
          var filteredItems = this.state.addedItems.filter((elem) => elem.id !== item.id);
          this.state.addedItems = filteredItems
        }
      }) 
    });
  };

  /**
   * Main method to render the page
   */
  render() {

    return <div>
      <Header/>

      <Jumbtron/>


      { (!this.state.checkout) ? 

      /* Section displays Items in Catalog  */
      <div className="container pt-4">
        <h2 className="display-4 text-center text-muted">Asset Catalog</h2>
        <div className="row my-4">
          <Checkout itemCount={this.state.itemCount} itemPrice={this.state.totalPrice} onClickCheckout={this.onClickCheckout} />
        </div>

        <div className="row">
          {
            this.state.items.map((item, index) =>
                <ItemCard
                    key = {item.id}
                    index = {index}
                    item = {item}
                    toggleItem = {() => this.toggleItemAddition(index)}
                    displayItemDetails = {() => this.displayItemDetails(index)}
                />
            )
          }
        </div>
        <div className="row">
          <Checkout itemCount={this.state.itemCount} itemPrice={this.state.totalPrice} onClickCheckout={this.onClickCheckout} />
        </div>
      </div>

      :
       /* Section Post Item Checkout  */
        <div className="container pt-4">
          <div className="row">
            <div className="col-lg">
              <h4 className="text-top text-muted">Assets Purchased</h4>
              {
                this.state.addedItems.map((item, index) =>
                    <PurchasedItem
                        key = {item.id}
                        index = {index}
                        item = {item}
                        removeItem = {() => this.removeCheckoutItem(index)}
                    />
                )
              }
            </div>
            <div class="col-sm">
              <h4 className="text-top text-muted">Payment Details</h4>
              <Payment itemPrice={this.state.totalPrice} onClickPay={this.onClickCheckout} />
            </div>
          </div>
        </div>
      }

      <hr/>
      <Footer/>

    </div>;
  }
}

export default App;
