import React from 'react';
import PropTypes from 'prop-types';

import {AssetCategory} from "./AssetCategory"
import {AddItem} from "./AddItem";
import {ItemCard} from "./ItemCard";

export const SellerView = ({state, addItem, onChangeItem, editItem, submitItem, handleFileCount}) => (
  <div>
    <div className="container">
        <div className="row mb-2">
          <AddItem 
            state={state}
            handleFileCount={handleFileCount}
            toggleButton={addItem}
            onSubmit={submitItem}
          />
        </div>
      <div className="row">
          {
            state.sellerItems.map((item, index) =>
                <ItemCard
                    key = {item.id}
                    item = {item}
                    toggleItem = {() => addItem(index)}
                    editItem = {() => editItem(index)}
                    editable = "true"
                />
            )
          }
        </div>
    </div>
  </div>
);

SellerView.propTypes = {
  state: PropTypes.shape({
    displayAddItem: PropTypes.bool.isRequired,
    imageFileCount: PropTypes.number.isRequired
  }),

  addItem: PropTypes.func.isRequired,
  editItem: PropTypes.func.isRequired,
  submitItem: PropTypes.func.isRequired,
  handleFileCount: PropTypes.func.isRequired,
};
