import React from 'react';
import PropTypes from 'prop-types';

import {AssetCategory} from "./AssetCategory"
import {AddItem} from "./AddItem";
import {ItemCard} from "./ItemCard";

export const SellerView = ({state, addItem, editItem, submitItem}) => (
  <div>
    <div className="container">
        <div className="row mb-2">
          <AddItem 
            state={state}
            displayForm={state.displayAddItem}
            onClick={addItem}
            onSubmit={submitItem}
          />
        </div>
      <div className="row">
          {
            state.sellerItems.map((item, index) =>
                <ItemCard
                    key = {item.id}
                    item = {item}
                    toggleItem = {() => editItem(index)}
                    editItem = {() => editItem(index)}
                    editable = "true"
                />
            )
          }
        </div>
      <div className="row">
        <form className="form-inline" onSubmit={submitItem}>
          <input
              type="text"
              className="form-control mb-2 mr-sm-2"
              placeholder="Item"
              value={state.name}
              name="name"
          />
          <button type="submit" className="btn btn-primary mb-2 pxy-4">Upload</button>
        </form>
      </div>
    </div>
  </div>
);

SellerView.propTypes = {
  state: PropTypes.object.isRequired,
  addItem: PropTypes.func.isRequired,
  editItem: PropTypes.func.isRequired,
  submitItem: PropTypes.func.isRequired
};
